// Prepare client data for onboarding
const client = $input.first().json;

// Generate unique client ID
const clientId = 'CLI-' + Date.now().toString(36).toUpperCase();

// Calculate onboarding timeline
const startDate = new Date();
const kickoffDate = new Date(startDate);
kickoffDate.setDate(startDate.getDate() + 2);

const setupCompleteDate = new Date(startDate);
setupCompleteDate.setDate(startDate.getDate() + 7);

const checkInDate = new Date(startDate);
checkInDate.setDate(startDate.getDate() + 14);

return {
  client_id: clientId,
  company_name: client.company_name,
  contact_name: client.contact_name,
  contact_email: client.contact_email,
  contact_phone: client.contact_phone || '',
  package: client.package || 'standard',
  start_date: startDate.toISOString().split('T')[0],
  kickoff_date: kickoffDate.toISOString().split('T')[0],
  setup_complete_date: setupCompleteDate.toISOString().split('T')[0],
  checkin_date: checkInDate.toISOString().split('T')[0],
  status: 'onboarding',
  created_at: startDate.toISOString(),

  // Onboarding checklist items
  checklist: [
    { task: 'Send welcome email', due_days: 0, status: 'pending' },
    { task: 'Create client folder', due_days: 0, status: 'pending' },
    { task: 'Schedule kickoff call', due_days: 1, status: 'pending' },
    { task: 'Send intake questionnaire', due_days: 1, status: 'pending' },
    { task: 'Kickoff call', due_days: 2, status: 'pending' },
    { task: 'System access setup', due_days: 3, status: 'pending' },
    { task: 'Initial configuration', due_days: 5, status: 'pending' },
    { task: 'Training session', due_days: 7, status: 'pending' },
    { task: 'Go-live support', due_days: 10, status: 'pending' },
    { task: '2-week check-in', due_days: 14, status: 'pending' }
  ]
};

